mapper.select:
SELECT DISTINCT p.`name` from ums_user_role ur
                                  LEFT JOIN ums_role r on ur.role_id = r.id
                                  LEFT JOIN ums_role_permission rp on ur.role_id =rp.role_id
                                  LEFT JOIN ums_permission p  on p.id = rp.permission_id
where ur.user_id= #{userId}
  and r.`status`=1;
